e-trigger-test-mode .log{height:100px;background:white;font-family:Courier New,Courier,monospace;overflow:auto}.live-trigger-test-mode .log .item{padding:2px 5px;border-bottom:1px solid lightgray}.live-trigger-test-mode .log .item.info{color:#55595c}.live-trigger-test-mode .log .item.success{color:#006400;background:#ddffdd}.live-trigger-test-mode .log .item.warn{color:#d2691e;background:beige}.live-trigger-test-mode .log .item.error{color:red;background:#ffdfdf}.live-trigger-test-mode .log .item.api{color:#0074d9}.live-trigger-test-mode span{display:block;clear:both;text-align:center}.live-trigger-test-mode span.live-trigger-test-mode-head{color:#fff}.live-trigger-test-mode span.live-trigger-test-mode-deactivate{color:#000}
